package com.rubenlozano.prueba.data.datasource.local.room.daoimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Queryimport com.rubenlozano.prueba.data.datasource.local.room.entity.UsersEntityimport kotlinx.coroutines.flow.Flow@Daointerface UsersDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertUsers(usersEntity: List<UsersEntity>)    @Query("SELECT * FROM UsersEntity")    fun getAllUsers(): Flow<List<UsersEntity>>}