package com.rubenlozano.prueba.data.datasource.remote.apiimport com.rubenlozano.prueba.utils.Constants.NATimport com.rubenlozano.prueba.utils.Constants.USERS_COUNTimport retrofit2.Responseimport retrofit2.http.GETimport retrofit2.http.Queryimport com.rubenlozano.prueba.data.datasource.remote.response.UsersResponseinterface Api {    @GET("api/")    suspend fun getAllUsers(        @Query("nat") nationality: String = NAT,        @Query("results") count: Int = USERS_COUNT,    ): Response<UsersResponse>}