package com.rubenlozano.prueba.domain.usecaseimport com.rubenlozano.prueba.data.model.Userimport com.rubenlozano.prueba.domain.repository.UsersRepositoryimport kotlinx.coroutines.flow.Flowimport javax.inject.Injectclass UsersUseCaseImpl @Inject constructor(    private val repository: UsersRepository,) : UsersUseCase {    override fun invoke(): Flow<List<User>> = repository.getAllUser()}