package com.rubenlozano.prueba.presentation.mainimport com.rubenlozano.prueba.data.model.Userimport com.rubenlozano.prueba.presentation.detail.DetailScreenimport com.rubenlozano.prueba.utils.navigation.AppNavigatorimport javax.inject.Injectinterface MainDirection {    suspend fun navigateToDetail(model: User)}class MainDirectionImpl @Inject constructor(    private val appNavigator: AppNavigator,) : MainDirection {    override suspend fun navigateToDetail(model: User) {        appNavigator.openWithSave(DetailScreen(model))    }}