package com.rubenlozano.prueba.presentation.main.componentimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.heightimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.sizeimport androidx.compose.foundation.layout.widthimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material3.Surfaceimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.clipimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport com.bumptech.glide.integration.compose.ExperimentalGlideComposeApiimport com.bumptech.glide.integration.compose.GlideImageimport com.bumptech.glide.integration.compose.placeholderimport com.rubenlozano.prueba.Rimport com.rubenlozano.prueba.ui.theme.BlueApp@OptIn(ExperimentalGlideComposeApi::class)@Composablefun HomeItems(    dniNumber:String,    gender: String,    nameSurname: String,    location: String,    email: String,    age: Int,    phoneNumber: String,    picture: String,    nationality: String,    onClick: () -> Unit = {},) {    Surface(        modifier = Modifier.fillMaxSize(),        shadowElevation = 4.dp,    ) {        Row(            modifier = Modifier                .fillMaxSize()                .padding(0.dp,0.dp, 0.dp, 1.dp)                .background(BlueApp)                .clickable { onClick() }        ) {            GlideImage(                model = picture,                contentDescription = "",                modifier = Modifier                    .size(110.dp)                    .padding(10.dp)                    .clip(shape = RoundedCornerShape(8.dp)),                loading = placeholder(R.drawable.ic_launcher_background),            )            Spacer(modifier = Modifier.width(16.dp))            Column {                Spacer(modifier = Modifier.height(15.dp))                Text(                    text = nameSurname,                    style = TextStyle(                        fontSize = 20.sp,                        fontWeight = FontWeight.Bold,                    ),                )                Spacer(modifier = Modifier.height(10.dp))                Text(                    text = location,                    style = TextStyle(                        fontSize = 16.sp,                        color = Color.White,                    ),                )                Spacer(modifier = Modifier.height(10.dp))                Text(                    text = email,                    style = TextStyle(                        fontSize = 16.sp,                        color = Color.White,                    ),                )            }        }    }}@Composable@Preview(showBackground = true)fun HomeItemsPreview() {    HomeItems(        dniNumber = "02856421F",        gender = "Mas",        nameSurname = "Ruben Lozano Muñoz",        location = "Toledo, España",        email = "ruben@gmail.com",        age = 30,        phoneNumber = "658742135",        picture = "",        nationality = "ES"    )}