package com.rubenlozano.prueba.utils.navigationimport kotlinx.coroutines.flow.MutableSharedFlowimport javax.inject.Injectimport javax.inject.Singleton@Singletonclass AppNavigatorImpl @Inject constructor() : AppNavigator, AppNavigatorHandler {    override val uiNavigator = MutableSharedFlow<NavigatorArgs>()    private suspend fun nav(navigatorArgs: NavigatorArgs) {        uiNavigator.emit(navigatorArgs)    }    override suspend fun openWithSave(myScreen: MyScreen) = nav {        push(myScreen)    }    override suspend fun back() = nav {        pop()    }}